sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},

	isDatePast: function(strDate) {
		var rightnow = new GlideDateTime();
		var whenNeeded = new GlideDateTime(strDate);

		if(whenNeeded.before(rightnow)){
			return true;
		} else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		var today = new GlideDateTime().getLocalDate();
		var isToday = new GlideDateTime(strDate).getLocalDate();

		if(today.compareTo(isToday) == 0) {
			return true;
		} else {
			return false;
		}
	},

	type: 'NeedItUtils'
};
